const input = $input.all();

function decode(str) {
  try {
    return JSON.parse(`"${str}"`);
  } catch (e) {
    return str;
  }
}

function fixNewLines(str) {
  return decode(str).replace(/\\n/g, `
  `);
}

const sections = [
  { title: "Introduction", content: fixNewLines(input[0].json.output) },
  { title: "Campaign Definition", content: fixNewLines(input[1].json.output) },
  { title: "Campaign Objectives", content: fixNewLines(input[2].json.output) },
  { title: "Current Situation Analysis", content: fixNewLines(input[3].json.output) },
  { title: "Target Audience Persona", content: fixNewLines(input[4].json.output) },
  { title: "Key Messaging", content: fixNewLines(input[5].json.output) },
  { title: "Main Marketing Strategy", content: fixNewLines(input[6].json.output) },
  { title: "guerrilla Marketing Tactics", content: fixNewLines(input[7].json.output) },
  { title: "Channels and Distribution", content: fixNewLines(input[8].json.output) },
  { title: "Execution Plan", content: fixNewLines(input[9].json.output) },
  { title: "Budget Planning", content: fixNewLines(input[10].json.output) },
  { title: "Monitoring and KPIs", content: fixNewLines(input[11].json.output) },
  { title: "Risk Management", content: fixNewLines(input[12].json.output) },
  { title: "Dos and Don'ts", content: fixNewLines(input[13].json.output) },
  { title: "Final To-Do List", content: fixNewLines(input[14].json.output) },
  { title: "Post Campaign Activities", content: fixNewLines(input[15].json.output) }
];

let text = "";

sections.forEach(section => {
  text += `# ${section.title}
${section.content}

`;
});

return {
  raw_output: sections,
  text: text
};
