const inputData = $input.all()[0];
console.log('Input data structure:', JSON.stringify(inputData, null, 2));

let data = [];

// 2) Try finding the data in different possible structures
try {
  if (inputData.json && inputData.json.query) {
    // If there's a "query" field with JSON data
    data = JSON.parse(inputData.json.query);
  } 
  else if (inputData.json) {
    // If the JSON is directly in the input
    data = inputData.json;
  } 
  else {
    // If all else fails, treat the entire input as data
    data = inputData;
  }

  // 3) Ensure "data" is an array
  if (!Array.isArray(data)) {
    // If data.items is an array
    if (data.items && Array.isArray(data.items)) {
      data = data.items;
    }
    // If it's a single object, wrap it
    else if (typeof data === 'object') {
      data = [data];
    }
    // If none of the above, default to empty array
    else {
      data = [];
    }
  }
} catch (error) {
  console.log('Error parsing data:', error.message);
  data = [];
}

// 4) Map in a custom UUID for each entry
const dataWithId = data.map((item, index) => {
  const uniqueId = `${Date.now()}-${index}-${Math.floor(Math.random() * 1000)}`;
  return { 
    UUID: uniqueId, 
    ...item 
  };
});

// 5) Return each entry as its own "item" in n8n
return dataWithId.map(entry => {
  return {
    json: entry
  };
});

