// DUPLICATE DETECTION WITH GUARANTEED UUID GENERATION

// 1. Get all items from the Merge node
const allItems = $input.all();

// 2. Find the existing business data from Google Sheets
let existingBusinesses = [];
const existingBusinessesItem = allItems.find(item => 
  item.json && item.json.source === "google_sheets_data"
);

if (existingBusinessesItem && existingBusinessesItem.json.businesses) {
  existingBusinesses = existingBusinessesItem.json.businesses;
  console.log(`Found ${existingBusinesses.length} existing businesses from Google Sheets`);
}

// 3. Create a set for quick lookup of existing businesses
const existingBusinessSet = new Set();
existingBusinesses.forEach(business => {
  // Create a normalized key from business name and address
  if (business.Name && business.Address) {
    const key = createBusinessKey(business);
    existingBusinessSet.add(key);
  }
});

console.log(`Created set with ${existingBusinessSet.size} unique existing businesses`);

// 4. Track duplicates within this batch
const batchBusinessSet = new Set();
const filteredOutput = [];

// 5. Find the new business data from Parse Content
const newBusinessItems = allItems.filter(item => 
  item.json && item.json.source === "parsed_content"
);

// 6. Process each new business
for (const item of newBusinessItems) {
  if (!item.json.businesses || !Array.isArray(item.json.businesses)) {
    continue;
  }

  for (const business of item.json.businesses) {
    if (!business.Name || !business.Address) {
      console.log("Skipping business with missing Name or Address");
      continue;
    }

    // Create a normalized key for this business
    const key = createBusinessKey(business);

    // Check if it's a duplicate (either exists in sheets or seen in this batch)
    const isDuplicate = existingBusinessSet.has(key) || batchBusinessSet.has(key);

    // ALWAYS generate a new UUID - guaranteed for all businesses
    const uuid = generateUUID();

    if (isDuplicate) {
      console.log(`Duplicate found: ${business.Name} at ${business.Address}`);
      filteredOutput.push({
        json: {
          ...business,
          UUID: uuid,  // Add new UUID
          _isDuplicate: true
        }
      });
    } else {
      console.log(`New unique business: ${business.Name} at ${business.Address}`);
      // Add to batch set to prevent duplicates within this batch
      batchBusinessSet.add(key);
      filteredOutput.push({
        json: {
          ...business,
          UUID: uuid,  // Add new UUID
          _isDuplicate: false
        }
      });
    }
  }
}

// 7. Check if all items are duplicates and add a status item if needed
const anyNonDuplicates = filteredOutput.some(item => item.json._isDuplicate === false);

// If all items are duplicates, add a status item that will pass through the If node
if (!anyNonDuplicates) {
  console.log("All items are duplicates - adding status item to ensure workflow continues");

  filteredOutput.push({
    json: {
      Name: "Status Update",
      Address: "N/A",
      UUID: `status-${Date.now()}`,  // Give status item a special UUID
      _isDuplicate: false,
      _isStatusItem: true,
      message: "All items were duplicates - no new businesses to add"
    }
  });
}

// Output statistics
console.log(`Processing complete:`);
console.log(`- Total businesses processed: ${filteredOutput.length}`);
console.log(`- Unique businesses: ${filteredOutput.filter(b => !b.json._isDuplicate && !b.json._isStatusItem).length}`);
console.log(`- Duplicates found: ${filteredOutput.filter(b => b.json._isDuplicate).length}`);

// Helper function to create a consistent business key
function createBusinessKey(business) {
  // Normalize name and address to create a consistent key
  const name = (business.Name || "").toLowerCase().replace(/[^a-z0-9]/g, "");
  const address = (business.Address || "").toLowerCase().replace(/[^a-z0-9]/g, "");
  return `${name}-${address}`;
}

// Helper function to generate UUID - format: timestamp-randomNumber
function generateUUID() {
  const timestamp = Date.now();
  const randomNum = Math.floor(Math.random() * 1000);
  return `${timestamp}-${randomNum}`;
}

// Return the filtered businesses
return filteredOutput;

// Format the Google Sheets data for merging
const sheetsData = $input.all();
let businesses = [];

// Process all items from Google Sheets
if (sheetsData && sheetsData.length > 0) {
  // Loop through all input items to collect all businesses
  for (const item of sheetsData) {
    if (!item.json) continue;

    // If item.json is an array, add all entries
    if (Array.isArray(item.json)) {
      console.log(`Found array of ${item.json.length} items in Google Sheets data`);
      businesses = businesses.concat(item.json);
    }
    // If item.json has an items property that's an array, add all entries
    else if (item.json.items && Array.isArray(item.json.items)) {
      console.log(`Found array of ${item.json.items.length} items in Google Sheets data.items`);
      businesses = businesses.concat(item.json.items);
    }
    // If item.json is a single business object with Name and Address
    else if (item.json.Name && item.json.Address) {
      console.log(`Found single business: ${item.json.Name}`);
      businesses.push(item.json);
    }
  }
}

// Log and output all found businesses
console.log(`Total of ${businesses.length} businesses found in Google Sheets data`);
if (businesses.length > 0) {
  console.log(`First business: ${businesses[0].Name}`);
  if (businesses.length > 1) {
    console.log(`Last business: ${businesses[businesses.length-1].Name}`);
  }
}

// Return ALL businesses in the formatted data
return [{
  json: {
    source: "google_sheets_data",
    businesses: businesses
  }
}];

// Format the parsed business data
const parsedItems = $input.all();
const businesses = [];

// Extract all valid businesses from the parsed content
for (const item of parsedItems) {
  // Check if we have query data (which appears to be an array of businesses)
  if (item.json && item.json.query) {
    try {
      // If query is a string containing JSON, parse it
      if (typeof item.json.query === 'string' && item.json.query.trim().startsWith('[')) {
        const queryData = JSON.parse(item.json.query);
        if (Array.isArray(queryData)) {
          console.log(`Found ${queryData.length} businesses in query JSON string`);
          for (const business of queryData) {
            if (business.Name && business.Address) {
              businesses.push(business);
            }
          }
        }
      } 
      // If query is already an array
      else if (Array.isArray(item.json.query)) {
        console.log(`Found ${item.json.query.length} businesses in query array`);
        for (const business of item.json.query) {
          if (business.Name && business.Address) {
            businesses.push(business);
          }
        }
      }
      // If query is a single business object
      else if (typeof item.json.query === 'object' && item.json.query.Name && item.json.query.Address) {
        console.log("Found a single business in query object");
        businesses.push(item.json.query);
      }
    } catch (error) {
      console.log(`Error parsing query data: ${error.message}`);
    }
  }
  // Legacy check: if item.json has direct business properties
  else if (item.json && item.json.Name && item.json.Address) {
    businesses.push(item.json);
  }
}

console.log(`Prepared ${businesses.length} businesses from parsed content`);

// Return the formatted data
return [{
  json: {
    source: "parsed_content",
    businesses: businesses
  }
}];
