const allItems = $input.all();
let data = [];
allItems.forEach(item => {
  const binaryData = item.binary;
  if (binaryData) {
    Object.keys(binaryData).forEach(key => {
      // MIME-Types für PDF und Word
      const allowedMimeTypes = [
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
      ];

      if (allowedMimeTypes.includes(binaryData[key]?.mimeType)) {
        data.push({
          binary: { data: binaryData[key] },
          json: {
            fileName: binaryData[key].fileName,
            mimeType: binaryData[key].mimeType
          }
        });
      }
    });
  }
});
return data;

