const aiAgentOutput = $input.all().map((item) => item.json.output)[0];
const chunkSize = 4096;

// Escape Markdown special characters (including the pipe `|`):
// This example is minimal—add more if you need to handle *all* Markdown chars.
const safeOutput = aiAgentOutput
  .replace(/\|/g, '\\|')
  .replace(/\*/g, '\\*')
  .replace(/_/g, '\\_');
// ...etc. for any other symbols that cause trouble

const chunks = [];
for (let i = 0; i < safeOutput.length; i += chunkSize) {
  chunks.push(safeOutput.slice(i, i + chunkSize));
}

return chunks.map((chunk) => ({ json: { output: chunk } }));

